import algoliasearch from 'algoliasearch'
import 'regenerator-runtime/runtime'
import './styles.css'

// Variables
let client, index
let appId = '', indexName = '', apiKey = '', pid = '', query = ''

// Elements
const form = document.getElementById('search-form')
const message = document.getElementById('search-message')
const link = document.getElementById('search-link')
const results = document.getElementById('search-results')

// Functions
const fetchRules = async query => {
  try {
    const { hits, nbHits } = await index.searchRules(query)

    if (nbHits === 0) return []

    return hits.map(({ description, objectID: id, tags }) => ({
      id,
      editor: tags?.includes('visual-editor') ? 'visual' : 'manual',
      description: description ?? '',
    }))
  } catch (e) {
    message.innerHTML = `
      <p class="text-danger fb-4">
        Your search returned an error 🫥 <br> 
        Please check the specified data and try again.
      </p>
    `
    link.innerHTML = ''
    results.innerHTML = ''

    throw e
  }
}

const getSearchUrl = query => {
  const pidParam = pid ? `&pid=${pid}` : ''
  return `https://www.algolia.com/apps/${appId}/explorer/browse/${indexName}?query=${query}&searchMode=search${pidParam}`
}

const getRuleUrl = (id, editor) => {
  const pidParam = pid ? `?pid=${pid}` : ''
  return `https://www.algolia.com/apps/${appId}/rules/${indexName}/${editor}-editor/edit/${id}${pidParam}`
}

// Search
form.addEventListener('submit', async e => {
  e.preventDefault()

  const _appId = e.target.querySelector('#app_id').value.trim()
  const _indexName = e.target.querySelector('#index_name').value.trim()
  const _apiKey = e.target.querySelector('#api_key').value.trim()
  const _pid = e.target.querySelector('#pid').value.trim()
  const _query = e.target.querySelector('#query').value.trim()

  if (_appId !== appId || _indexName !== indexName || _apiKey !== apiKey) {
    client = algoliasearch(_appId, _apiKey)
    index = client.initIndex(_indexName)
    appId = _appId
    indexName = _indexName
    apiKey = _apiKey
  }
  pid = _pid

  if (_query.toLowerCase() === query.toLowerCase() && _appId !== appId && _indexName !== indexName && _apiKey !== apiKey) return
  query = _query

  // Fetch rules
  const rules = await fetchRules(query)

  // Link to dashboard
  link.innerHTML = `<a href="${getSearchUrl(query)}" target="_blank">See this search in the dashboard</a>`

  // No rules
  if (rules.length === 0) {
    message.innerHTML = `<p class="text-danger">No rules are applying with the query "${query}"</p>`
    results.innerHTML = ''
    return
  }
  // Rules
  message.innerHTML = `
    <p class="text-success">${rules.length} rule${rules.length > 1 ? 's are' : ' is'} applying with the query "${query}"</p>
  `
  results.innerHTML = `
    <table class="table">
      <thead>
        <tr>
          <th scope="col">Rule ID</th>
          <th scope="col">Type</th>
          <th scope="col">Description</th>
          <th scope="col">URL</th>
        </tr>
      </thead>
      <tbody>
        ${rules.map(
          ({ id, editor, description }) => `
          <tr>
            <td>${id}</td>
            <td>${`${editor[0].toUpperCase()}${editor.slice(1)}`}
            <td>${description}</td>
            <td><a href="${getRuleUrl(id, editor)}" target="_blank">Link to rule</a></td>
          </tr>
        `
        )}
      </tbody>
    </table>
  `
})
